mapper.select:
SELECT bno, title, contents, writer, regdate, updatedate, image FROM stsboard
writer = #{ writer } AND
bno = #{ bno }

mapper.insert:

INSERT INTO stsboard (
               bno
               , title
               , contents
               , writer
               
               , regdate
               
               
               , updatedate
               
               
               , image
               
               ) 
         VALUES (
               #{bno}
               , #{title}
               , #{contents}
               , #{writer}
               
               , #{regdate}
               
               
               , #{updatedate}
               
               
               , #{image}
               
               )
